mapper.insert:
INSERT INTO tb_order (orderNo, createTime, userId)
VALUES(
	#{orderNo},
	#{createTime},
	#{userId}
)

mapper.update:
UPDATE tb_order SET
	totalMoney = #{totalMoney}
WHERE orderId = #{orderId}

UPDATE tb_order SET
	address = #{address},
	receiver = #{receiver},
	phone = #{phone}
WHERE orderId = #{orderId}

UPDATE tb_order SET
	state = #{state}
WHERE orderId = #{orderId}
